mapper.select:
select id from tb_checkin where user_id = #{userId}
and date = #{date}
and create_time between #{start} and #{end}
limit 1;

select
DATE_FORMAT(create_time,'%d %H:%i') AS checkinTime
from tb_checkin where user_id = #{userId}
and date = CURRENT_DATE
and create_time between #{start} and #{end}
limit 1;

SELECT COUNT(*) AS count
FROM tb_checkin
WHERE user_id=#{userId}

SELECT
ci.status as checkInStatus, co.status as checkOutStatus, co.work_time as workTime FROM tb_checkin as ci left join tb_checkout as co on ci.user_id = co.user_id and co.date = #{date}
WHERE ci.user_id=#{userId} and ci.date = #{date}
